import "./app.css";
import Habits from "./components/habits";
import Navbar from "./components/navbar";
import React, { useCallback, useState } from "react";

const App = (props) => {
	let getHabitlist;
	localStorage.getItem("habitlist")
		? (getHabitlist = JSON.parse(localStorage.getItem("habitlist")))
		: (getHabitlist = []);

	const [habits, setHabits] = useState(getHabitlist);

	//변경되는 속성이
	const handleIncrement = useCallback((habit) => {
		setHabits((habits) =>
			habits.map((item) => {
				if (item.id === habit.id) {
					return { ...habit, count: habit.count + 1 };
				}
				return item;
			})
		);
		const list = JSON.stringify(habits);
		localStorage.setItem("habitlist", list);
	}, []);

	const handleDecrement = (habit) => {
		const habitList = habits.map((item) => {
			if (item.id === habit.id) {
				return {
					...habit,
					count: habit.count > 0 ? habit.count - 1 : 0,
				};
			}
			return item;
		});
		const list = JSON.stringify(habitList);
		localStorage.setItem("habitlist", list);
		setHabits(habitList);
	};
	const handleDelete = (habit) => {
		//id가 같지 않은 요소들로만 다시 배열을 만들어준다
		const habitList = habits.filter((item) => item.id !== habit.id);
		const list = JSON.stringify(habitList);
		localStorage.setItem("habitlist", list);
		setHabits(habitList);
	};

	const onAdd = (name) => {
		const habitList = [...habits, { id: Date.now(), name, count: 0 }];
		const list = JSON.stringify(habitList);
		localStorage.setItem("habitlist", list);
		setHabits(habitList);
	};
	const handleReset = () => {
		const habitList = habits.map((habit) => {
			if (habit.count !== 0) {
				return { ...habit, count: 0 };
			}
			return habit;
		});
		const list = JSON.stringify(habitList);
		localStorage.setItem("habitlist", list);
		setHabits(habitList);
	};

	const habit = habits;
	return (
		<React.StrictMode>
			<Navbar totalCount={habit.filter((item) => item.count > 0).length} />
			<Habits
				habit={habit}
				onIncrement={handleIncrement}
				onDecrement={handleDecrement}
				onDelete={handleDelete}
				onAdd={onAdd}
				onReset={handleReset}
			/>
		</React.StrictMode>
	);
};

export default App;
