import "./app.css";
import Habits from "./components/habits";
import Navbar from "./components/navbar";
import React, { useState, useCallback } from "react";

const App = (props) => {
	let getHabitlist;
	localStorage.getItem("habitlist")
		? (getHabitlist = JSON.parse(localStorage.getItem("habitlist")))
		: (getHabitlist = []);

	const [habits, setHabits] = useState(getHabitlist);

	const handleIncrement = useCallback((habit) => {
		setHabits((habits) =>
			habits.map((item) => {
				if (item.id === habit.id) {
					return { ...habit, count: habit.count + 1 };
				}
				return item;
			})
		);
		const list = JSON.stringify(habits);
		localStorage.setItem("habitlist", list);
	}, []);

	const handleDecrement = useCallback((habit) => {
		setHabits((habits) =>
			habits.map((item) => {
				if (item.id === habit.id) {
					return {
						...habit,
						count: habit.count > 0 ? habit.count - 1 : 0,
					};
				}
				return item;
			})
		);
	}, []);
	const handleDelete = useCallback((habit) => {
		//id가 같지 않은 요소들로만 다시 배열을 만들어준다
		setHabits((habits) => habits.filter((item) => item.id !== habit.id));
	}, []);

	const onAdd = useCallback((name) => {
		setHabits((habits) => [...habits, { id: Date.now(), name, count: 0 }]);
		const list = JSON.stringify(habits);
		localStorage.setItem("habitlist", list);
	}, []);

	const handleReset = useCallback(() => {
		setHabits((habit) =>
			habits.map((habit) => {
				if (habit.count !== 0) {
					return { ...habit, count: 0 };
				}
				return habit;
			})
		);
	}, []);

	return (
		<React.StrictMode>
			<Navbar totalCount={habits.filter((item) => item.count > 0).length} />
			<Habits
				habit={habits}
				onIncrement={handleIncrement}
				onDecrement={handleDecrement}
				onDelete={handleDelete}
				onAdd={onAdd}
				onReset={handleReset}
			/>
		</React.StrictMode>
	);
};

export default App;
