import Habit from "./habit";
import HabitAddForm from "./HabitAddForm";
import React from "react";

const Habits = (props) => {
	const handleIncrement = (habit) => {
		props.onIncrement(habit);
	};
	const handleDecrement = (habit) => {
		props.onDecrement(habit);
	};
	const handleDelete = (habit) => {
		props.onDelete(habit);
	};
	const handleAdd = (name) => {
		props.onAdd(name);
	};

	const habit = props.habit;
	console.log(props);
	return (
		<div>
			<HabitAddForm habit={habit} onAdd={handleAdd} />
			<ul>
				{habit.map((habit) => (
					//habit 이라는 props에 habit 요소를 전달 해준다
					<Habit
						key={habit.id}
						habit={habit}
						onIncrement={handleIncrement}
						onDecrement={handleDecrement}
						onDelete={handleDelete}
					/>
				))}
			</ul>
			<button type="button" className="habits-reset" onClick={props.onReset}>
				Reset
			</button>
		</div>
	);
};

export default Habits;
